package LECTURE1;

public class Arthmatical_oprator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int a=10;
     int b=5;
//     System.out.print(a +""+ b);
     int c=a+b;
     System.out.println(c);
     c=a-b;
     System.out.println(c);
     int d=a/b;
     System.out.println(d);
     d=a%b;
     System.out.println(d);
     
	}

}
